function processInput(input) {
  for (const item of input.all()) {

    if (item.json['Link Folder']) {
      const linkFolder = item.json['Link Folder'];
      const uniqueLinks = [];
      const seen = {}; // Đối tượng để theo dõi các giá trị đã xuất hiện

      for (const link of linkFolder) {
        if (!seen[link]) {
          uniqueLinks.push(link);
          seen[link] = true;
        }
      }

      item.json['Link Folder'] = uniqueLinks;
    }
  }

  return input.all();
}

return processInput($input);
